import React, { useState, useEffect } from 'react';
import DropdownList from "react-widgets/lib/DropdownList";

import { listAuthors } from '../../services/authors';

function AuthorDropdown({ value, onChange }) {
    const [authors, setAuthors] = useState([]);

    useEffect(() => {
        const fetchAuthors = async () => {
            const data = await listAuthors();

            setAuthors(data.map((author) => ({...author, fullName: `${author.firstName} ${author.lastName}`}) ))
        };

        fetchAuthors();
    }, []);



    return (
        <div className="AuthorDropdown">
            { authors.length > 1 ?  <DropdownList
                defaultValue={ authors[0].id}
                value={ value }
                data={ authors }
                textField= "fullName"
                valueField="id"
                onChange={ onChange }
                allowCreate={ false }
            /> : undefined }

            {/* <>
            <DropdownList
                defaultValue="2"
                data={[{id: "1", "name": "Redo" }, {id: "2", "name": "Yellow" }, {id: "3", "name": "Orange" } ]}
                textField= "name"
                valueField="id"
            />
            </>; */}
        </div>
    );
}

export default AuthorDropdown;
