import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Table from 'react-bootstrap/Table';

import { ROUTE_AUTHOR_PREFIX, ROUTE_AUTHOR_CREATE } from '../../constants';
import { listAuthors } from '../../services/authors';

function AuthorList() {
    const [authors, setAuthors] = useState([]);

    useEffect(() => {
        const fetchAuthors = async () => {
            const data = await listAuthors();
            setAuthors(data);
        };

        fetchAuthors();
    }, []);

    const renderArticles = () => authors.map((author) => {
        const { id, firstName, lastName } = author;

        return (
            <tr key={ id } >
                <td>
                    <Link to={ `${ROUTE_AUTHOR_PREFIX}/${id}` }><strong>{ firstName } { lastName }</strong></Link>
                </td>
            </tr>
        );
    });

    return (
        <div className="AuthorList">
            <h1>Authos</h1>
            <Link className="d-block mb-3" to={ ROUTE_AUTHOR_CREATE }>
                Create a new Author
            </Link>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th><h6>Authors</h6></th>
                    </tr>
                </thead>
                <tbody>
                    { (renderArticles()) }
                </tbody>
            </Table>
        </div>
    );
}

export default AuthorList;
